import { makeAutoObservable } from "mobx"

export default class UserStore {
    constructor(){
        this._isAuth= false
        this._user= {}
        this._role={}
        makeAutoObservable(this)
    }
    setRole(role){
        this._role=role
    }
    get role(){
        return this._role
    }
setIsAuth(bool) {
    this._isAuth = bool
}

setUser(user) {
    this._user = user
}

get isAuth() {
    return this._isAuth
}
get user() {
    return this._user
}
}
