import { observer } from 'mobx-react-lite';
import { action } from 'mobx';
import React, { useContext } from 'react'
import {Routes, Route, Navigate} from 'react-router-dom'
import { Context } from '../index.js';
import { authRoutes, publicRoutes, adminRoutes } from '../routes';
import jwt_decode from "jwt-decode";
import { SHOP_ROUTE } from '../utils/consts';

const AppRouter = observer(() => {
    const {user} = useContext(Context)
    const {master} =useContext(Context)
    const token = localStorage.getItem('token');

    if (token) {
        const decodedToken = jwt_decode(token);
        console.log(decodedToken); // отладочный вывод
        let setUserRole = action((role) => {
            user.setRoleUser(decodedToken.role);
        });

    }

    console.log(user)
    console.log(user._roleUser)
    console.log(user._user)
    return (
        <Routes>
            {setUserRole==="ADMIN" && adminRoutes.map(({path, Component}) =>
                <Route key={path} path={path} element={<Component/>} exact/>
            )}
        {user._isAuth && authRoutes.map(({path, Component}) =>
          <Route key={path} path={path} element={<Component/>} exact/>
        )}
        {publicRoutes.map(({path, Component}) =>
        <Route key={path} path={path} element={<Component/>} exact/>
        )}
        <Route path='*' element={<Navigate to={SHOP_ROUTE}/>} />
      </Routes>
    );
});
 export default AppRouter
