import React, { useContext } from 'react'
import { Context } from '../index.js';
import { NavLink } from 'react-router-dom';
import { ABOUTUS_ROUTE, ADMIN_ROUTE, MASTER_ROUTE, SHOP_ROUTE, LOGIN_ROUTE } from '../utils/consts.js';
import { observer } from 'mobx-react-lite';
import styles from './NavBar.module.css';
import { useNavigate } from "react-router-dom"
import jwt_decode from "jwt-decode";



const NavBar = observer(() => {
    const token = localStorage.getItem('token');
    const decodedToken = jwt_decode(token);
    const userRole = decodedToken.role;
  const navigate = useNavigate()
    const {user} = useContext(Context)
    const {master} =useContext(Context)
    const logOut= () => {
      user.setUser({})
        user.setIsAuth(false)
    }
    return (
<nav>
<NavLink onClick={() => {
    master.setSelectedType({});
    master.setIsMasters(false);
}} className={styles.active} to={SHOP_ROUTE}>BARBERSHOP</NavLink>
      {user.isAuth ?
      <ul>
      <li>
      <NavLink onClick={() => {
          master.setSelectedType({});
          master.setIsMasters(false);
      }} className={styles.activelink} to={ABOUTUS_ROUTE}>О нас</NavLink >
        </li>
        <li>
        <NavLink className={styles.activelink} to={MASTER_ROUTE}>Мастера</NavLink>
        </li>
        <li>
        |
        </li>
          {userRole ==="ADMIN" && <li>
            <button className={styles.navbutton} onClick={()=>navigate(ADMIN_ROUTE)}>Админ панель</button>
        </li>}
        <li>
        <button  className={styles.navbutton} onClick={()=> {
            navigate(LOGIN_ROUTE);
            logOut();
        }
        }>Выйти</button>
        </li>
        </ul>
      :
      <ul>
      <li>
      <NavLink className={styles.activelink} to={ABOUTUS_ROUTE}>О нас</NavLink >
        </li>
        <li>
        <NavLink className={styles.activelink} to={MASTER_ROUTE}>Мастера</NavLink>
        </li>
        <li>
        |
        </li>
      <li>
          <button onClick={() =>navigate(LOGIN_ROUTE)} className={styles.navbutton}>Авторизация</button>
        </li>
        </ul>
      }
       
    </nav>
  );
});
 export default NavBar
