import {$authHost, $host} from "./index";

export const createMasters = async (masters) => {
    const {data} = await $authHost.post('api/masters', masters)
    return data
}
export function deleteMasterByName(masterName) {
    fetch(`/api/masters/${masterName}`, {
        method: 'DELETE'
    })
        .then(response => response.json())
        .then(data => {
            console.log(data.message); // Мастер успешно удалён
        })
        .catch(error => {
            console.error(error);
        });
}




export const fetchMasters = async () => {
    const {data} = await $host.get('api/masters')
    console.log(data);
    return data
}
